/** @format */

import { createStore } from "redux";
import { createSlice } from "@reduxjs/toolkit";

const initialState = { counter: 0, showCounter: true };

//needs an object as an argument
const counterSlice = createSlice({
	name: "counter",
	initialState,
	//reducers also receive an object or you could say a map
	reducers: {
		increment(state) {
			state.counter++;
		},
		decrement(state) {
			state.counter--;
		},
		increase(state, action) {
			state.counter = state.counter + action.amount;
		},
		toggleCounter(state) {
			//this will inverte the state if true to false and if false to true
			state.showCounter = !state.showCounter;
		},
	},
});

//the create store receives a parameter
//this creates our react store
const store = createStore(counterSlice.reducer);

export default store;
