/** @format */

import { createSlice, configureStore } from "@reduxjs/toolkit";

const initialCounterState = { counter: 0, showCounter: true };

//needs an object as an argument
const counterSlice = createSlice({
	name: "counter",
	initialState: initialCounterState,
	//reducers also receive an object or you could say a map
	reducers: {
		increment(state) {
			state.counter++;
		},
		decrement(state) {
			state.counter--;
		},
		increase(state, action) {
			state.counter = state.counter + action.payload;
		},
		toggleCounter(state) {
			//this will inverte the state if true to false and if false to true
			state.showCounter = !state.showCounter;
		},
	},
});

const initialAuthState = {
	isAutheticated: false,
};

const authSlice = createSlice({
	name: "authentication",
	initialState: initialAuthState,
	reducers: {
		login(state) {
			state.isAutheticated = true;
		},
		logout(state) {
			state.isAutheticated = false;
		},
	},
});

//the create store receives a parameter an object a configured object
//this creates our react store
const store = configureStore({
	reducer: {
		reducer: counterSlice.reducer,
		reducer: authSlice.reducer,
	},
});
export const counterActions = counterSlice.actions;

export const authActions = authSlice.actions;

export default store;
